// Daily boss rotation function - UPDATED to use actual holobots from HOLOBOT_DATA
export const getDailyBossRotation = () => {
  // Create deterministic boss rotations based on the day of the year
  const now = new Date();
  const start = new Date(now.getFullYear(), 0, 0);
  const diff = now.getTime() - start.getTime();
  const oneDay = 1000 * 60 * 60 * 24;
  const dayOfYear = Math.floor(diff / oneDay);
  
  // Get all available holobots from HOLOBOT_DATA
  const availableHolobots = Object.keys(HOLOBOT_DATA).filter(key => 
    typeof HOLOBOT_DATA[key] === 'object' &&
    HOLOBOT_DATA[key]?.name !== undefined
  );
  
  // Define the tiers with actual holobots
  const bossRotations = {
    tier1: availableHolobots.slice(0, 5).map(h => h.toLowerCase()),
    tier2: availableHolobots.slice(5, 10).map(h => h.toLowerCase()),
    tier3: availableHolobots.slice(10, 15).map(h => h.toLowerCase())
  };
  
  // If we don't have enough holobots for all tiers, reuse some
  if (bossRotations.tier2.length < 5) {
    bossRotations.tier2 = [...bossRotations.tier2, ...bossRotations.tier1.slice(0, 5 - bossRotations.tier2.length)];
  }
  
  if (bossRotations.tier3.length < 5) {
    bossRotations.tier3 = [...bossRotations.tier3, ...bossRotations.tier1.slice(0, 5 - bossRotations.tier3.length)];
  }
  
  return {
    tier1: bossRotations.tier1[dayOfYear % bossRotations.tier1.length],
    tier2: bossRotations.tier2[dayOfYear % bossRotations.tier2.length],
    tier3: bossRotations.tier3[dayOfYear % bossRotations.tier3.length]
  };
};

